package javacourse1;
import java.util.Scanner;

public class forloopquestion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	    Scanner S =new Scanner(System.in);
	    int n = S.nextInt();
	    for(int i = 1; i<=n; i = i+1) {
	     if (i%2 ==0) {
	    	 System.out.println(i);
	    	 
	     }
	    }

	}

}
